import * as React from 'react';
import { StyleSheet, Text, View, SafeAreaView, ScrollView, Image } from 'react-native';
import SelectDropdown from 'react-native-select-dropdown';
import Ionic from 'react-native-vector-icons/Ionicons';

const from = ["Meeting", "Appiontments", "Field visits","Maintaining Files", "Welcoming visitors to office", "Answering phone calls", "Takng and Delivering messages","Ensuring the office runs smoothly"]

export default function OfficeWork() {
    return (
        <SafeAreaView style={styles.container}>
            <ScrollView>
                <View >
                    <Text style={styles.Heading}> Office Works </Text>

                    
                    <Image style={styles.img}
                    source={{
                        uri:'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQRaM-7GTBOHbhRYiTerbP0NzWeJaYDNuTpCg&usqp=CAU',
                        width:'100%',
                        height:200,
                    }}
                    />
                    <SelectDropdown
                data={from}
                onSelect={(selectedItem, index) => {
                    console.log(selectedItem, index)
                }}
                defaultButtonText={'Meeting'}
                buttonTextAfterSelection={(selectedItem, index) => {
                    return selectedItem
                }}
                rowTextForSelection={(item, index) => {
                    return item
                }}
                buttonStyle={styles.dropdownStyle}
                buttonTextStyle={styles.dropdownTxtStyle}
                renderDropdownIcon={isOpened => {
                    return <Ionic name={isOpened ? 'chevron-up' : 'chevron-down'} color={'maroon'} size={20} />
                }}
                dropdownIconPosition={'right'}
                dropdownStyle={styles.dropdownStyle1}
                rowStyle={styles.dropdownRowStyle}
                rowTextStyle={styles.dropdownRowTxtStyle}
            />
                    
                </View>
            </ScrollView>
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        padding:20,
    },
    Heading: {
        fontSize: 50,
        fontWeight: 'bold',
        color: 'blue',
        textAlign: 'center',
    },
    img:{
        marginBottom:20,
    },
    dropdownStyle: {
        width: '98%',
        height: 60,
        backgroundColor: '#FFF',
        borderRadius: 8,
        borderWidth: 2,
        borderColor: 'blue',
    },
    dropdownTxtStyle: { 
        color: '#444', 
        textAlign: 'left' 
    },
    dropdownStyle1: { 
        backgroundColor: '#EFEFEF' 
    },
    dropdownRowStyle: { 
        backgroundColor: '#EFEFEF', 
        borderBottomColor: '#C5C5C5' 
    },
    dropdownRowTxtStyle: { 
        color: '#444', 
        textAlign: 'left' 
    },
});
